<script>
import { mapGetters } from "vuex";

export default {
  data() {
    return {
      scrollY: 0,
    };
  },
  computed: {
    navActive() {
      return this.scrollY > 0 ? true : false;
    },
    ...mapGetters("cart", {
      count: "count",
    }),
  },
  mounted() {
    window.addEventListener("scroll", this.onScroll);
  },
  beforeDestroy() {
    window.removeEventListener("scroll", this.onScroll);
  },
  methods: {
    onScroll() {
      this.scrollY = window.scrollY;
    },
  },
};
</script>

